List booleans = new ArrayList();
booleans.add(true);
booleans.add(false);
qo.addFilter(Filter.in("workgroup", booleans));
qo.setScopeResults(true);
qo.addOrdering("firstname", true);
qo.addOrdering("lastname", true);
qo.addOrdering("name", true);
qo.addOrdering("id", true);
return qo;

