import sailpoint.object.ProvisioningPlan;
import sailpoint.object.ProvisioningPlan.AccountRequest;
import sailpoint.object.ProvisioningPlan.AttributeRequest;
import sailpoint.object.Bundle;
import java.util.List;
import java.util.ArrayList;

try {
   /**
    ** BusinessRole is a string which defines a business role present in identityiq environment.
    ** User need to change this as per their need.**/
    String businessRole = "businessRole";

    ProvisioningPlan plan = new ProvisioningPlan();
    Identity identityObject = context.getObjectByName(Identity.class,
    identityName);
    if(log.isDebugEnabled())
       log.debug(" ---------Identity object is --------" + identityObject);

    List assignedRolesList = new ArrayList();

    /**
     ** Getting business role from context for the given businessRole variable above.
     **/
    Bundle bd = (Bundle) context.getObjectByName(Bundle.class, businessRole);
    assignedRolesList.add(bd);
    List accreqs = new ArrayList();
    AccountRequest acctReq = new AccountRequest(AccountRequest.Operation.Modify,
    ProvisioningPlan.APP_IIQ, null, identityName);
    acctReq.setOperation(AccountRequest.Operation.Modify);

    for (Bundle arole: assignedRolesList) {
        roleName = arole.getFullName();
        if (log.isDebugEnabled()) 
           log.debug(" ---------- Assigning role ------ " + roleName);
        acctReq.add(new AttributeRequest(ProvisioningPlan.ATT_IIQ_ASSIGNED_ROLES, ProvisioningPlan.Operation.Add, roleName));
    }
    accreqs.add(acctReq);
    plan.setAccountRequests(accreqs);
    if (log.isDebugEnabled()) 
        log.debug("-------Plan is -------- " + plan.toXml());
    return plan;
} catch (Exception e) {
   if(log.isErrorEnabled()) 
        log.error(e.getMessage());
    throw new Exception("Please check task result for more details. "+e.getMessage());
}

