import java.util.List;
import java.util.Iterator;
import sailpoint.tools.Util;
List schemes = Util.csvToList(approvalScheme);
Iterator it = schemes.iterator();
while (it.hasNext()) {
  String s = it.next();
  if (!s.equals(approvalSplitPoint)) {
      it.remove();
  } else {
      break;
  }
}
return Util.listToCsv(schemes);

/** Below is the script for mover workflow. This mover workflow users Identity attribute "department" for mover event.
**  Basically this is an attribute change kind of Lifecycle event. 
**  Variable in the script "moverAttribute" is the name of the attribute based on which mover event is triggered.
**  Logically this script is assigning any of the requested roles "businessRoleIT" or "businessRoleHR" to an Identity to whom initially
**  no business roles are assigned.
**  If already an identity have any one of the business role assigned, then while assigment of new role, the older business role will be removed
**  and new one will be assigned.
*/

   import sailpoint.object.ProvisioningPlan;
   import sailpoint.object.ProvisioningPlan.AccountRequest;
   import sailpoint.object.ProvisioningPlan.AttributeRequest;
   import sailpoint.object.Bundle;
   import sailpoint.object.RoleRequest;
   import java.util.List;
   import java.util.ArrayList;
   import sailpoint.tools.Util;

   try {
               String moverAttribute = "department";
               String itDepartment = "IT";
               String hrDepartment = "HR";
               String businessRoleForITDepartment = "businessRoleIT";
               String businessRoleForHRDepartment = "businessRoleHR";
               ProvisioningPlan plan = new ProvisioningPlan();
               List accreqs = new ArrayList();
               Bundle bundle = null;
               String wrongDepartmentSelected = "Mover Process Exception - Wrong department selected. Valid departments are  '" + itDepartment + "' or '" + hrDepartment +"'";
               boolean isDepartmentCorrect = false;
               Identity identityObject = context.getObjectByName(Identity.class,identityName);
               Object departmentObject = identityObject.getAttribute(moverAttribute);
               String department = Util.otos(departmentObject);
               if(Util.isNullOrEmpty(department)) {                        
                   throw new Exception("Mover Process Exception - Identity attribute " + moverAttribute + " Not found in identity " + identityName);
               }
               if(department.equals(itDepartment))
                   isDepartmentCorrect = true;
               else if(department.equals(hrDepartment))
                   isDepartmentCorrect = true;
               if(!isDepartmentCorrect) {
                   throw new Exception(wrongDepartmentSelected);
               }
               /*
               * Getting all bundles assgined to user.
               */
               List<Bundle> assignedBundles = identityObject.getAssignedRoles();
               /**
                * Creating bundle objects for all business roles requested
                */
                Bundle bundleIT = (Bundle) context.getObjectByName(Bundle.class, businessRoleForITDepartment);
                Bundle bundleHR = (Bundle) context.getObjectByName(Bundle.class, businessRoleForHRDepartment);

                AccountRequest acctReq = new AccountRequest(AccountRequest.Operation.Modify, ProvisioningPlan.APP_IIQ, null, identityName);
                acctReq.setOperation(AccountRequest.Operation.Modify);

                /**
                 * If no business roles are assigned to the Identity, then requested business role is directly assigned to identity.
                */
                if(Util.isEmpty(assignedBundles)) {
                       if(department.equals(itDepartment)) {
                           acctReq.add(new AttributeRequest(ProvisioningPlan.ATT_IIQ_ASSIGNED_ROLES, ProvisioningPlan.Operation.Add,bundleIT.getName()));
                       }
                       if(department.equals(hrDepartment)) {
                           acctReq.add(new AttributeRequest(ProvisioningPlan.ATT_IIQ_ASSIGNED_ROLES, ProvisioningPlan.Operation.Add, bundleHR.getName())); 
                       } 
                }

               else { 
               /**
                * If Identity has already any of the business role already assigned then action is to remove the assigned and assign the new 
                * requeste business role.
                */
                      for(Bundle bd : assignedBundles) {
                           String roleName = bd.getName();
                           if(department.equals(itDepartment)) {
                               if(roleName.equals(bundleHR.getName())) {
                                   acctReq.add(new AttributeRequest(ProvisioningPlan.ATT_IIQ_ASSIGNED_ROLES, ProvisioningPlan.Operation.Remove,bundleHR.getName()));
                                   break;
                                }
                           }
                           if(department.equals(hrDepartment)) {
                               if(roleName.equals(bundleIT.getName())) {
                                   acctReq.add(new AttributeRequest(ProvisioningPlan.ATT_IIQ_ASSIGNED_ROLES, ProvisioningPlan.Operation.Remove,bundleIT.getName()));
                                   break;
                                }
                           }
                   }

                   if(department.equals(itDepartment)) {
                               acctReq.add(new AttributeRequest(ProvisioningPlan.ATT_IIQ_ASSIGNED_ROLES, ProvisioningPlan.Operation.Add,bundleIT.getName()));  
                           }
                   if(department.equals(hrDepartment)) {
                               acctReq.add(new AttributeRequest(ProvisioningPlan.ATT_IIQ_ASSIGNED_ROLES, ProvisioningPlan.Operation.Add, bundleHR.getName()));
                           } 
               }

               accreqs.add(acctReq);
               plan.setAccountRequests(accreqs);
               if (log.isDebugEnabled()) 
                   log.debug("-------Plan is -------- " + plan.toXml());
               return plan;
   } catch (Exception e) {
               if(log.isErrorEnabled()) 
                   log.error(e.getMessage());
               throw new Exception("Please check task result for more details. " + e.getMessage());
   }

